var contentArr = [
  { title: 'company website', href: '' },
  { title: 'github', href: 'https://github.com/Afante-Studios-AGC' },
  { title: 'codepen', href: '' },
  { title: 'facebook', href: 'https://www.facebook.com/astroxp.io' },
  { title: 'google+', href: '' },
  { title: 'twitter', href: 'https://twitter.com/astroxpofficial' },
  { title: 'discord', href: 'https://discord.gg/astroxpio' },
  { title: 'instagram', href: '' },
]
let instagramColor =
  'radial-gradient(circle at 35% 90%, rgb(254, 197, 100), transparent 50%), radial-gradient(circle at 0px 140%, rgb(254, 197, 100), transparent 50%), radial-gradient(at 0px -25%, rgb(82, 88, 207), transparent 50%), radial-gradient(at 20% -50%, rgb(82, 88, 207), transparent 50%), radial-gradient(at 100% 0px, rgb(137, 61, 194), transparent 50%), radial-gradient(at 60% -20%, rgb(137, 61, 194), transparent 50%), radial-gradient(at 100% 100%, rgb(217, 49, 122), transparent), rgba(0, 0, 0, 0) linear-gradient(rgb(101, 89, 202), rgb(188, 49, 143) 30%, rgb(227, 63, 95) 50%, rgb(247, 118, 56) 70%, rgb(254, 198, 109) 100%)'
var items = $('.social-item'),
  container = $('.social-container'),
  title = $('.social-title'),
  link = $('.link'),
  prevElement = null
function getIconColor(getTitle, that) {
  return 'instagram' === getTitle
    ? instagramColor
    : $(that).css('background-color')
}
function colorInstagram(getTitle) {
  'instagram' === getTitle
    ? setTimeout(function () {
        $('.instagram-color').css('background', 'transparent')
      }, 164)
    : ($('.instagram-color').css('background-color'),
      $('.instagram-color').css('background', instagramColor))
}
items.on('mouseenter', function (e) {
  var itemTrigger = $(this)
  if (this !== prevElement) {
    prevElement = this
    var data = contentArr[$(e.currentTarget).attr('id')],
      getTitle = data.title
    title.text(getTitle),
      link.attr('href', data.href).addClass('link--visibility'),
      items.removeClass('current'),
      itemTrigger.addClass('current')
    var ripple = $("<span unselectable='on' class='ripple'></span>")
    container.append(ripple)
    var x = e.pageX - container.offset().left - ripple.width() / 2,
      y = e.pageY - container.offset().top - ripple.height() / 2
    let iconColor = getIconColor(getTitle, this)
    ripple.css({
      background: iconColor,
      width: 0,
      height: 0,
      left: x + 'px',
      top: y + 'px',
      'margin-left': 0,
      'margin-top': 0,
    })
    var r = Math.max(container.outerWidth(), container.outerHeight())
    ripple.animate(
      { width: 2 * r, height: 2 * r, 'margin-left': -r, 'margin-top': -r },
      {
        duration: 800,
        easing: 'easeInOutCubic',
        queue: !1,
        complete: function () {
          ripple.parent().css('background', iconColor),
            'instagram' != getTitle
              ? ripple.detach()
              : (ripple.fadeOut(500),
                setTimeout(function () {
                  ripple.detach()
                }, 500))
        },
      },
    ),
      colorInstagram(getTitle)
  }
})
